const modalBtn=document.querySelectorAll("[data-modal]"),body=document.body,modalClose=document.querySelectorAll(".modal__close"),modal=document.querySelectorAll(".modal");function closeModal(e){e.querySelector(".modal__content").removeAttribute("style"),setTimeout(()=>{e.classList.remove("show"),body.classList.remove("no-scroll")},200)}modalBtn.forEach(e=>{e.addEventListener("click",e=>{let o=e.currentTarget,t=o.getAttribute("data-modal"),l=document.getElementById(t),a=l.querySelector(".modal__content");a.addEventListener("click",e=>{e.stopPropagation()}),l.classList.add("show"),body.classList.add("no-scroll"),setTimeout(()=>{a.style.transform="none",a.style.opacity=1},1)})}),modalClose.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget.closest(".modal"))})}),modal.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget)})});